INSERT INTO `cms_article` (`id`, `shopid`, `title`, `info`, `author`, `picurl`, `content`, `disabled`, `publishAt`, `location`, `channelId`, `opBy`, `opAt`, `delFlag`) VALUES ('06ac3137d9ce4bd19b37ce4075c7192e', NULL, '团队介绍', '', '超级管理员', '', '<p><span style="font-size: 18px;">百匠万灵财务有着专业的只能团队，不光在专业的工商财务知识处于行业前端，我们更注重对客户的服务，我们大多数财务工商专业科班出身，在财务，工商税务，知识产权，等专业有着深厚的研究，对于国家政策，法律法规有着很深的学习，保护企业在经营过程中遇到政策问题，令企业专注自己的领域，其他的百匠帮您处理。</span><br/></p><p><br/></p><p style=";margin-top:0;margin-bottom:0;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align:baseline"><span style="font-family: 微软雅黑; color: black; font-size: 18px;">拥有会计专业：中级职称2人；</span></p><p style=";margin-top:0;margin-bottom:0;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align:baseline"><span style="font-family: 微软雅黑; color: black; font-size: 18px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 初级职称5人；</span></p><p style=";margin-top:0;margin-bottom:0;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align:baseline"><span style="font-family: 微软雅黑; color: black; font-size: 18px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 外贸会计1人；</span></p><p style=";margin-top:0;margin-bottom:0;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align:baseline"><span style="font-family: 微软雅黑; color: black; font-size: 18px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 从业资格3人。</span></p><p style=";margin-top:0;margin-bottom:0;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align:baseline"><span style="font-family: 微软雅黑; color: black; font-size: 18px;">其中，本科以上学历3人，大专以上4人。</span></p><p style=";margin-top:0;margin-bottom:0;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align:baseline"><span style="font-f