INSERT INTO `cms_article` (`id`, `shopid`, `title`, `info`, `author`, `picurl`, `content`, `disabled`, `publishAt`, `location`, `channelId`, `opBy`, `opAt`, `delFlag`) VALUES ('2fad00ab2bcb48e3aac3762e608fa7d3', NULL, '企业形象', '', '超级管理员', '', '<p><br/></p><p><br/></p><p><span style="text-decoration: none;"><span style="text-decoration: none; font-family: DengXian;">&nbsp; &nbsp; 百匠</span>-<span style="text-decoration: none; font-family: DengXian;">万灵财务咨询（上海）有限公司位于上海市浦东新区如日商务园，是一家专业从事企业注册、财税咨询代理等现代服务型公司。</span></span></p><p><span style="text-decoration: none;"><span style="text-decoration: none; font-family: DengXian;"><br/></span></span></p><p><span style="text-decoration: none;"><span style="text-decoration: none; font-family: DengXian;">&nbsp; &nbsp; 公司成立于</span>2007<span style="text-decoration: none; font-family: DengXian;">年。长期以来，为上海市奉贤区所辖各功能开发区域的招商引资和经济发展做出了卓越贡献。百匠接受众多海内外著名企业的委托，为其在沪设立申办各种类型的企业代理注册登记。百匠</span>-<span style="text-decoration: none; font-family: DengXian;">万灵财务咨询（上海）有限公司已经累计服务上万加企业，助力中小企业成长。</span></span></p><p><span style="text-decoration: none;"><span style="text-decoration: none;">&nbsp; &nbsp;&nbsp;</span><span style="text-decoration: none; font-family: DengXian;">百匠</span>-<span style="text-decoration: none; font-family: DengXian;">万灵财务咨询（上海）有限公司专注于中小企业服务，主要业务：企业注册、财务代理、人事服务、商标注册、商务中心、创业投资、企业年审，小企业融资，令企业专注自己的领域，其他的百匠为您保驾护航。</span></span></p><p><span style="text-decoration: none;"><span style="text-decoration: none; font-size: 15px; line-height: 107%; font-family: Calibri, sans-serif;">&nbsp;</span><span style="text-decoration: none; font-size: 15px; line-height: 107%; font-family: DengXian;">长久以来百匠</span><span style="text-decoration: none; font-size: 15px; line-height: 107%; font-family: Calibri, sans-serif;">-</span><span style="text-decoration: none; font-size: 15px; line-height: 107%; font-family: DengXian;">万灵财务咨询（上海）有限公司顺应国家政策，致力维护良好营商环境，减轻企业负担降低创业风险，提高企业存活率和成功率，帮助税务部门维护合法税务市场.</span></span></p><p><br/></p><p><img src="/nutzwk/upload/image/20180922/ud6na8f41ajcop7evfhm2fe1v5.jpg" title="" alt="7.jpg"/></p><p><img src="/nutzwk/upload/image/20180922/ruh3qc6lroj4bolmahtigvhcn2.png" title="" alt="6.png"/></p><p><img src="/nutzwk/upload/image/20180922/3cc2n5osfkgtkocrrqirb09tav.png" title="" alt="3.png"/></p><p><br/></p><p><br/></p>', 0, 1536988551, 62, '718ec4a50aba453c8225cebfa4562d30', 'be2040f6f2cd44259b9a80988549dd12', 1536988625, 0);
