INSERT INTO `cms_article` (`id`, `shopid`, `title`, `info`, `author`, `picurl`, `content`, `disabled`, `publishAt`, `location`, `channelId`, `opBy`, `opAt`, `delFlag`) VALUES ('81f5c3c73220449183c45fe2f8691ea7', NULL, '公司介绍', '', '超级管理员', '', '<p><br/></p><p><strong><span style="font-size: 18px;">匠，巧匠，鲁班也，谐将，喻匠心，百匠做，百匠闯，百灵动，是为百匠！</span></strong></p><p><br/></p><p><span style="font-size: 18px;"><span style="font-family: DengXian;">百匠</span>-<span style="font-family: DengXian;">万灵财务咨询（上海）有限公司位于上海市浦东新区如日商务园，是一家专业从事企业注册、财税咨询代理等现代服务型公司。公司成立于</span>2007<span style="font-family: DengXian;">年。长期以来，为上海市奉贤区所辖各功能开发区域的招商引资和经济发展做出了卓越贡献。百匠接受众多海内外著名企业的委托，为其在沪设立申办各种类型的企业代理注册登记。百匠</span>-<span style="font-family: DengXian;">万灵财务咨询（上海）有限公司已经累计服务上万加企业，助力中小企业成长。</span></span></p><p><span style="font-family: DengXian; font-size: 18px;"><br/></span></p><p><span style="font-size: 18px;"><span style="font-size: 18px; font-family: DengXian;">百匠</span>-<span style="font-size: 18px; font-family: DengXian;">万灵财务咨询（上海）有限公司专注于中小企业服务，主要业务：企业注册、财务代理、人事服务、商标注册、商务中心、创业投资、企业年审，小企业融资，令企业专注自己的领域，其他的百匠为您保驾护航。</span><span style="font-size: 15px; line-height: 107%; font-family: DengXian;">长久以来百匠</span><span style="font-size: 15px; line-height: 107%; font-family: Calibri, sans-serif;">-</span><span style="font-size: 15px; line-height: 107%; font-family: DengXian;">万灵财务咨询（上海）有限公司顺应国家政策，致力维护良好营商环境，减轻企业负担降低创业风险，提高企业存活率和成功率，帮助税务部门维护合法税务市场。</span></span></p><p><span style="line-height: 107%; font-family: DengXian; font-size: 18px;"><br/></span></p><p><span style="line-height: 107%; font-family: DengXian; font-size: 18px;">公司主营业务有：代理记账，企业登记代理，财务咨询，商标制作以及注册，企业变更，共享开票，税控盘的销售与维护等。<br/></span></p><p><span style="line-height: 107%; font-family: DengXian; font-size: 18px;"><br/></span></p><p><font face="宋体, SimSun"><span style="font-size: 20px;"><b>百匠万灵财务的优势：</b></span></font></p><p><span style="font-family: 宋体, SimSun; font-size: 16px;"><strong><span style="font-family: 宋体, SimSun; line-height: 107%;"><span style="font-family: 宋体, SimSun; line-height: 107%; color: black; font-weight: bold;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;退税：享受政府高额返税；</span>&nbsp;</span></strong></span></p><p><span style="font-size: 16px;"><span style="font-family: 宋体, SimSun;"><strong><span style="line-height: 107%; color: black; font-weight: bold;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;场地：无需提供注册场地；</span></strong><strong><span style="line-height: 107%; color: black; font-weight: bold;"></span></strong></span><strong><span style="line-height: 107%; font-family: 宋体, SimSun;"><span style="line-height: 107%; font-family: 楷体; color: black; font-weight: bold;"></span>&nbsp;</span></strong><strong><span style="line-height: 107%; font-family: 宋体, SimSun;"></span></strong></span></p><p><span style="color: black; font-weight: bold; line-height: 107%; font-family: 宋体, SimSun; font-size: 16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;补贴：办理费用全程补贴；</span></p><p><span style="font-size: 16px;"><span style="font-family: 宋体, SimSun;"><strong><span style="line-height: 107%; color: black; font-weight: bold;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;快速：加急办理快速出照；</span></strong><strong><span style="line-height: 107%; color: black; font-weight: bold;"></span></strong></span><strong><span style="line-height: 107%; font-family: 宋体, SimSun;"><span style="line-height: 107%; font-family: 楷体; color: black; font-weight: bold;"></span>&nbsp;</span></strong></span></p><p><span style="font-size: 16px;"><strong><span style="color: black; font-weight: bold; line-height: 107%; font-family: 宋体, SimSun;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;服务：专业团队为您服务;</span></strong><strong><span style="color: black; font-weight: bold; line-height: 107%; font-family: 宋体, SimSun;"></span></strong></span></p><p><span style="font-size: 15px; line-height: 107%; font-family: DengXian;"><br/></span